import openai



def get_response(prompt, model='gpt-3.5-turbo'):
    prompt = (f'### Instruction:\n Given the Input below, decide if the Input is an advertisement. Is the Input an advertisement? If it is, answer yes, otherwise answer no.\n\n'
              f'### Input:\n {prompt}\n\n'
              f'### Response:\n')

    messages = [{
        'role': 'user',
        'content': prompt
    }]

    response = openai.ChatCompletion.create(model=model, messages=messages, temperature=0)

    return response.choices[0].message['content']


def decode_response_binary(response):
    if 'yes' in response.lower():
        return 1
    else:
        return 0


def shorten_text(text, _max: int):
    split = text.split()
    split = split[:min(len(split), _max)]

    return ' '.join(split)


